import { React, useState } from "react";
import "./Main.css";
import Paper from "./images/icon-paper.svg";
import Rock from "./images/icon-rock.svg";
import Scissor from "./images/icon-scissors.svg";
import Logo from "./images/logo.svg";
import Triangle from "./images/bg-triangle.svg";
import Close from "./images/icon-close.svg";
import Rules from "./images/image-rules.svg";

export default function Main() {
  var [score, setScore] = useState(0);

  var [rules, setRules] = useState(false);

  var [bot, setBot] = useState("");

  var [played, setPlayed] = useState(false);

  function play(x) {
    var bot_choice = Math.floor(Math.random() * 3);

    setPlayed(true);
    // 0 = rock
    // 1 = paper
    // 2 = scissors

    if (
      (x == "rock" && bot_choice == 0) ||
      (x == "paper" && bot_choice == 1) ||
      (x == "scissors" && bot_choice == 2)
    ) {
      console.log("tie");
      return "tie";
    } else if (
      (x == "rock" && bot_choice == 1) ||
      (x == "paper" && bot_choice == 2) ||
      (x == "scissors" && bot_choice == 0)
    ) {
      console.log("loss");
      setScore(score - 1);
      return "loss";
    } else if (
      (x == "rock" && bot_choice == 2) ||
      (x == "paper" && bot_choice == 0) ||
      (x == "scissors" && bot_choice == 1)
    ) {
      console.log("win");
      setScore(score + 1);
      return "win";
    }
  }

  return (
    <div className="back">
      {rules ? (
        <div className="rules-shadow">
          <div className="rules-box">
            <div className="top-rules-flex">
              <span className="rules-title">RULES{rules}</span>
              <img
                src={Close}
                onClick={() => setRules(!rules)}
                className="close"
              />
            </div>
            <img src={Rules} />
          </div>
        </div>
      ) : (
        ""
      )}
      <div className="top-flex">
        <img src={Logo} />
        <div className="score-box">
          {" "}
          <span className="score-title">SCORE</span>{" "}
          <div className="score">{score}</div>
        </div>
      </div>
      {played ? (
        ""
      ) : (
        <div className="img-box">
          <img src={Triangle} className="triangle" />
          <div className="top-imgs">
            <div onClick={() => setTimeout(() => play("paper"), 300)}>
              <div className="img-border-b">
                <div className="img-holder">
                  <img src={Paper} className="img" />
                </div>
              </div>
            </div>
            <div onClick={() => setTimeout(() => play("paper"), 300)}>
              <div className="img-border-y">
                <div className="img-holder">
                  <img
                    src={Scissor}
                    className="img"
                    onClick={() => setTimeout(() => play("scissors"), 3000)}
                  />
                </div>
              </div>
            </div>
          </div>
          <div className="lower-imgs">
            {" "}
            <div onClick={() => setTimeout(() => play("paper"), 300)}>
              <div className="img-border-r">
                <div className="img-holder">
                  <img
                    src={Rock}
                    className="img"
                    onClick={() => setTimeout(() => play("rock"), 3000)}
                  />
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      {rules ? (
        ""
      ) : (
        <div className="rules" onClick={() => setRules(!rules)}>
          RULES
        </div>
      )}
    </div>
  );
}
